{world_bank:{_ID:{_OID:'52b213b38594d8a2be17c78a'},APPROVALFY:'2014',BOARD_APPROVAL_MONTH:'October',BOARDAPPROVALDATE:'2013-10-25T00:00:00Z',BORROWER:'GOVERNMENT OF SOUTH SUDAN',COUNTRY_NAMECODE:'Republic of South Sudan!_!SS',COUNTRYCODE:'SS',COUNTRYNAME:'Republic of South Sudan',COUNTRYSHORTNAME:'South Sudan',DOCTY:'Project Paper,Project Information Document',ENVASSESMENTCATEGORYCODE:'B',GRANTAMT:7530000,IBRDCOMMAMT:0,ID:'P145339',IDACOMMAMT:0,IMPAGENCY:'MINISTRY OF AGRICULTURE, COOPERATIVES AND RURAL DEVELOPMENT',LENDINGINSTR:'Specific Investment Loan',LENDINGINSTRTYPE:'IN',LENDPROJECTCOST:7530000,MAJORSECTOR_PERCENT:{__NAME_PERCENT:{NAME:'Agriculture, fishing, and forestry',PERCENT:50},__NAME_PERCENT:{NAME:'Health and other social services',PERCENT:30},__NAME_PERCENT:{NAME:'Agriculture, fishing, and forestry',PERCENT:20}},MJSECTOR_NAMECODE:{__CODE_NAME:{NAME:'Agriculture, fishing, and forestry',CODE:'AX'},__CODE_NAME:{NAME:'Health and other social services',CODE:'JX'},__CODE_NAME:{NAME:'Agriculture, fishing, and forestry',CODE:'AX'}},MJTHEME:{__CHAR_VALUE:'Rural development'},MJTHEME_NAMECODE:{__CODE_NAME:{NAME:'Rural development',CODE:'10'},__CODE_NAME:{NAME:'',CODE:'2'}},MJTHEMECODE:'10,2',PRODLINE:'RE',PRODLINETEXT:'Recipient Executed Activities',PRODUCTLINETYPE:'L',PROJECT_ABSTRACT:{CDATA:'The development objective of the Additional Financing (AF) for the Emergency Food Crisis Response Project for South Sudan is to support adoption of improved technologies for food production by eligible beneficiaries, increase storage capacity for staples, and provide cash or food to eligible people participating in public works programs in selected counties in South Sudan. This is the third AF to the project and will be primarily used to scale-up and augment benefits to already participating beneficiaries and to expand project activities to four additional counties where recent monitoring points to significantly deteriorating food security. The AF will cover the costs associated with: (i) provision of agricultural inputs, production technology, and advisory services; (ii) rehabilitating a seed processing facility to increase farmer s access to improved seed; (iii) bringing land that is currently out of production back into production; (iv) training farmers on reduction of postharvest losses; (v) building of food storage capacity to support postharvest handling at the household and community levels; and (vi) provision of cash or food for work to eligible individuals. The implementation schedule will be slightly revised and the closing date of both the original project and AF will be extended to April 30, 2015.'},PROJECT_NAME:'Southern Sudan Emergency Food Crisis Response Project- AF III',PROJECTDOCS:{__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Paper (PJPR),  Vol.1 of 1',DOCTYPE:'PJPR',ENTITYID:'000442464_20131009102446',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000442464_20131009102446',DOCDATE:'01-OCT-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Information Document (PID),  Vol.1 of 1',DOCTYPE:'PID',ENTITYID:'000001843_20130618091419',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000001843_20130618091419',DOCDATE:'07-JUN-2013'}},PROJECTFINANCIALTYPE:'OTHER',PROJECTSTATUSDISPLAY:'Active',REGIONNAME:'Africa',SECTOR:{__NAME:{NAME:'Crops'},__NAME:{NAME:'Other social services'},__NAME:{NAME:'General agriculture, fishing and forestry sector'}},SECTOR1:{NAME:'Crops',PERCENT:50},SECTOR2:{NAME:'Other social services',PERCENT:30},SECTOR3:{NAME:'General agriculture, fishing and forestry sector',PERCENT:20},SECTOR_NAMECODE:{__CODE_NAME:{NAME:'Crops',CODE:'AH'},__CODE_NAME:{NAME:'Other social services',CODE:'JB'},__CODE_NAME:{NAME:'General agriculture, fishing and forestry sector',CODE:'AZ'}},SECTORCODE:'AZ,JB,AH',SOURCE:'IBRD',STATUS:'Active',SUPPLEMENTPROJECTFLG:'Y',THEME1:{NAME:'Global food crisis response',PERCENT:100},THEME_NAMECO