{world_bank:{_ID:{_OID:'52b213b38594d8a2be17c7ff'},APPROVALFY:'2013',BOARD_APPROVAL_MONTH:'June',BOARDAPPROVALDATE:'2013-06-18T00:00:00Z',BORROWER:'REPUBLIC OF HAITI',CLOSINGDATE:'2014-06-30T00:00:00Z',COUNTRY_NAMECODE:'Republic of Haiti!_!HT',COUNTRYCODE:'HT',COUNTRYNAME:'Republic of Haiti',COUNTRYSHORTNAME:'Haiti',DOCTY:'Implementation Status and Results Report,Financing Agreement,Program Document,Summary of Discussion,Program Document,Project Information Document',GRANTAMT:0,IBRDCOMMAMT:0,ID:'P127208',IDACOMMAMT:20000000,IMPAGENCY:'MINISTRY OF FINANCE',LENDINGINSTR:'Development Policy Lending',LENDINGINSTRTYPE:'AD',LENDPROJECTCOST:20000000,MAJORSECTOR_PERCENT:{__NAME_PERCENT:{NAME:'Public Administration, Law, and Justice',PERCENT:56},__NAME_PERCENT:{NAME:'Energy and mining',PERCENT:28},__NAME_PERCENT:{NAME:'Public Administration, Law, and Justice',PERCENT:11},__NAME_PERCENT:{NAME:'Finance',PERCENT:5}},MJSECTOR_NAMECODE:{__CODE_NAME:{NAME:'Public Administration, Law, and Justice',CODE:'BX'},__CODE_NAME:{NAME:'Energy and mining',CODE:'LX'},__CODE_NAME:{NAME:'Public Administration, Law, and Justice',CODE:'BX'},__CODE_NAME:{NAME:'Finance',CODE:'FX'}},MJTHEME:{__CHAR_VALUE:'Public sector governance',__CHAR_VALUE:'Public sector governance',__CHAR_VALUE:'Public sector governance'},MJTHEME_NAMECODE:{__CODE_NAME:{NAME:'Public sector governance',CODE:'2'},__CODE_NAME:{NAME:'Public sector governance',CODE:'2'},__CODE_NAME:{NAME:'Public sector governance',CODE:'2'}},MJTHEMECODE:'2,2,2',PRODLINE:'PE',PRODLINETEXT:'IBRD/IDA',PRODUCTLINETYPE:'L',PROJECT_ABSTRACT:{CDATA:'The objective of the Economic Reconstruction and Growth Development Policy Grant Project for Haiti is to support the Government of Haiti s program of sustainable reconstruction and growth. Towards this end, the grant supports institution building and the strengthening of economic governance in sectors critical to reconstruction and growth in the short and medium term. The grant is a single tranche operation that builds on the institutional foundations laid under the past series of Development Policy Grant (DPGs), some of which were stalled or reversed by the earthquake. The earthquake increased the urgency to address Haiti s deep structural problems of weak economic governance and inefficient and nontransparent public resource use. The significant inflow of reconstruction funds not only created pressure on the existing capacity to manage and use funds, but also increased demand for better economic governance.'},PROJECT_NAME:'Economic Reconstruction and Growth Development Policy Credit',PROJECTDOCS:{__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Implementation Status and Results Report (ISRR),  Vol.',DOCTYPE:'ISRR',ENTITYID:'090224b081f44f79_1_0',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=090224b081f44f79_1_0',DOCDATE:'01-OCT-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Financing Agreement (FIA),  Vol.',DOCTYPE:'FIA',ENTITYID:'090224b081df338e_1_0',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=090224b081df338e_1_0',DOCDATE:'27-JUN-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Program Document (PGD),  Vol.1 of 1',DOCTYPE:'PGD',ENTITYID:'000445729_20130702104342',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000445729_20130702104342',DOCDATE:'26-JUN-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Summary of Discussion (Summary of Discussion),  Vol.1 of 1',DOCTYPE:'Summary of Discussion',ENTITYID:'000333037_20130628104303',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000333037_20130628104303',DOCDATE:'18-JUN-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Program Document (PGD),  Vol.1 of 1',DOCTYPE:'PGD',ENTITYID:'000445729_20130530100927',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000445729_20130530100927',DOCDATE:'15-MAY-2013'},__DOCDATE