{world_bank:{_ID:{_OID:'52b213b38594d8a2be17c845'},APPROVALFY:'2013',BOARD_APPROVAL_MONTH:'May',BOARDAPPROVALDATE:'2013-05-14T00:00:00Z',BORROWER:'MOLDOVA SOCIAL INVESTMENT FUND',CLOSINGDATE:'2016-07-30T00:00:00Z',COUNTRY_NAMECODE:'Republic of Moldova!_!MD',COUNTRYCODE:'MD',COUNTRYNAME:'Republic of Moldova',COUNTRYSHORTNAME:'Moldova',DOCTY:'Implementation Status and Results Report,Project Information Document,Integrated Safeguards Data Sheet,Project Paper',ENVASSESMENTCATEGORYCODE:'F',GRANTAMT:2860000,IBRDCOMMAMT:0,ID:'P144618',IDACOMMAMT:0,IMPAGENCY:'MOLDOVA SOCIAL INVESTMENT FUND',LENDINGINSTR:'Specific Investment Loan',LENDINGINSTRTYPE:'IN',LENDPROJECTCOST:2860000,MAJORSECTOR_PERCENT:{__NAME_PERCENT:{NAME:'Health and other social services',PERCENT:80},__NAME_PERCENT:{NAME:'Education',PERCENT:20}},MJSECTOR_NAMECODE:{__CODE_NAME:{NAME:'Health and other social services',CODE:'JX'},__CODE_NAME:{NAME:'Education',CODE:'EX'}},MJTHEME:{__CHAR_VALUE:'Human development',__CHAR_VALUE:'Human development'},MJTHEME_NAMECODE:{__CODE_NAME:{NAME:'Human development',CODE:'8'},__CODE_NAME:{NAME:'Human development',CODE:'8'}},MJTHEMECODE:'8,8',PRODLINE:'RE',PRODLINETEXT:'Recipient Executed Activities',PRODUCTLINETYPE:'L',PROJECT_ABSTRACT:{CDATA:'The development objective of the Integration of Children with Disabilities into Mainstream Schools Project for Moldova is to demonstrate through pilot activities that local governments can successfully apply national policies that promote integration of children with disabilities into the mainstream education system. The project will integrate (that is, mainstream) children with disabilities into their community hub-schools and into community social activities. It will achieve this through local planning to apply national policies for inclusion of children with disabilities and through 20 demonstration (pilot) sub-projects to adapt mainstream schools, and to educate teachers and parents. The project could trigger additional financing from donors by demonstrating practical ways to apply progressive policy at the local level, the project will open the way for other donors and for Ministry of Education (MoE) to fund the integration of children with disabilities in further hub schools.'},PROJECT_NAME:'Integration of Children with Disabilities into Mainstream Schools',PROJECTDOCS:{__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Implementation Status and Results Report (ISRR),  Vol.',DOCTYPE:'ISRR',ENTITYID:'090224b081f65e20_1_0',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=090224b081f65e20_1_0',DOCDATE:'07-OCT-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Information Document (PID),  Vol.1 of 1',DOCTYPE:'PID',ENTITYID:'000333037_20130510115002',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000333037_20130510115002',DOCDATE:'25-MAR-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Integrated Safeguards Data Sheet (ISDS),  Vol.1 of 1',DOCTYPE:'ISDS',ENTITYID:'000333037_20130510115452',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000333037_20130510115452',DOCDATE:'13-MAR-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Paper (PJPR),  Vol.1 of 1',DOCTYPE:'PJPR',ENTITYID:'000356161_20130423112049',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000356161_20130423112049',DOCDATE:'01-MAR-2013'}},PROJECTFINANCIALTYPE:'OTHER',PROJECTSTATUSDISPLAY:'Active',REGIONNAME:'Europe and Central Asia',SECTOR:{__NAME:{NAME:'Other social services'},__NAME:{NAME:'General education sector'}},SECTOR1:{NAME:'Other social services',PERCENT:80},SECTOR2:{NAME:'General education sector',PERCENT:20},SECTOR_NAMECODE:{__CODE_NAME:{NAME:'Other social services',CODE:'JB'},__CODE_NAME:{NAME:'General education sector',CODE:'EZ'}},SECTORCODE:'EZ,JB',SOURCE:'IBRD',STATUS:'Active',SUPPLEMENTPROJECTFLG:'N',THEME1:{NAME:'Education for all',PERCENT:20},THE