{world_bank:{_ID:{_OID:'52b213b38594d8a2be17c84b'},APPROVALFY:'2013',BOARD_APPROVAL_MONTH:'May',BOARDAPPROVALDATE:'2013-05-09T00:00:00Z',BORROWER:'MINISTRY OF FINANCE',COUNTRY_NAMECODE:'Democratic Republic of the Congo!_!ZR',COUNTRYCODE:'ZR',COUNTRYNAME:'Democratic Republic of the Congo',COUNTRYSHORTNAME:'Congo, Democratic Republic of',DOCTY:'Project Paper,Integrated Safeguards Data Sheet,Project Information Document',ENVASSESMENTCATEGORYCODE:'C',GRANTAMT:0,IBRDCOMMAMT:0,ID:'P126115',IDACOMMAMT:66950000,IMPAGENCY:'MINISTRY OF DECENTRALIZATION',LENDINGINSTR:'Specific Investment Loan',LENDINGINSTRTYPE:'IN',LENDPROJECTCOST:66950000,MAJORSECTOR_PERCENT:{__NAME_PERCENT:{NAME:'Public Administration, Law, and Justice',PERCENT:80},__NAME_PERCENT:{NAME:'Public Administration, Law, and Justice',PERCENT:20}},MJSECTOR_NAMECODE:{__CODE_NAME:{NAME:'Public Administration, Law, and Justice',CODE:'BX'},__CODE_NAME:{NAME:'Public Administration, Law, and Justice',CODE:'BX'}},MJTHEME:{__CHAR_VALUE:'Public sector governance',__CHAR_VALUE:'Public sector governance',__CHAR_VALUE:'Public sector governance'},MJTHEME_NAMECODE:{__CODE_NAME:{NAME:'Public sector governance',CODE:'2'},__CODE_NAME:{NAME:'Public sector governance',CODE:'2'},__CODE_NAME:{NAME:'Public sector governance',CODE:'2'}},MJTHEMECODE:'2,2,2',PRODLINE:'PE',PRODLINETEXT:'IBRD/IDA',PRODUCTLINETYPE:'L',PROJECT_ABSTRACT:{CDATA:'The objective of the Governance Capacity Enhancement Project for Democratic Republic of Congo is to improve the quality of governance and support administrative decentralization in the Democratic Republic of Congo by (a) enhancing transparency and efficiency in central and sub-national public finance and human resource management; and (b) establishing and consolidating an equitable resource sharing mechanism between central and sub-national government. The project has three components: (1) Strengthening public financial management and public service management at central government level, including establishing a functioning system of intergovernmental fiscal relations, including equipment, technical assistance and minor refurbishment of facilities; (2) Building public sector and financial management systems at provincial level; (3) Governance reform implementation capacity by strengthening of the implementation units in the provinces, including the recruitment of procurement specialists.'},PROJECT_NAME:'Governance II',PROJECTDOCS:{__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Paper (PJPR),  Vol.1 of 1',DOCTYPE:'PJPR',ENTITYID:'000445729_20130422104317',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000445729_20130422104317',DOCDATE:'19-APR-2013'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Integrated Safeguards Data Sheet (ISDS),  Vol.1 of 1',DOCTYPE:'ISDS',ENTITYID:'000003596_20110707130344',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000003596_20110707130344',DOCDATE:'06-JUL-2011'},__DOCDATE_DOCTYPE_DOCTYPEDESC_DOCURL_ENTITYID:{DOCTYPEDESC:'Project Information Document (PID),  Vol.1 of 1',DOCTYPE:'PID',ENTITYID:'000104615_20110706125000',DOCURL:'http://www-wds.worldbank.org/servlet/WDSServlet?pcont=details&eid=000104615_20110706125000',DOCDATE:'15-JUN-2011'}},PROJECTFINANCIALTYPE:'IDA',PROJECTSTATUSDISPLAY:'Active',REGIONNAME:'Africa',SECTOR:{__NAME:{NAME:'Central government administration'},__NAME:{NAME:'Sub-national government administration'}},SECTOR1:{NAME:'Central government administration',PERCENT:80},SECTOR2:{NAME:'Sub-national government administration',PERCENT:20},SECTOR_NAMECODE:{__CODE_NAME:{NAME:'Central government administration',CODE:'BC'},__CODE_NAME:{NAME:'Sub-national government administration',CODE:'BH'}},SECTORCODE:'BH,BC',SOURCE:'IBRD',STATUS:'Active',SUPPLEMENTPROJECTFLG:'Y',THEME1:{NAME:'Administrative and civil service reform',PERCENT:33},THEME_NAMECODE:{__CODE_NAME:{NAME:'Administrative and civil service reform',CODE:'25'},__CODE_NAME:{NAME:'Dece